    /**
     * Create a elements with same height
     * Group elements with data-height attribute
     * All elements will have same height with the tallest one
     * @return {void}
     * @sample usage:
     *
     * <div data-height-xs="group1" data-height-sm="group1"></div>
     * <div data-height-xs="group1" data-height-sm="group1"></div>
     * <div data-height-xs="group1"></div>
     *
     * You can use this atttributes: data-height-xs, data-height-sm, data-height-md, data-height-lg
     * When device changes for example when user rotate the device, this is recalculated.
     */
    sameHeights: function(dataSelector) {
      var groups = {};

      $('[' + dataSelector + ']').css('height', '');
      $('[' + dataSelector + ']').each(function() {
        var $this = $(this),
          data = $this.data(dataSelector.replace('data-', '')),
          height = $this.outerHeight();

        if (!groups[data] || height > groups[data]) {
          groups[data] = height;
        }
      });

      $.each(groups, function(index, value) {
        $('[' + dataSelector + '="' + index + '"]').css('height', value + 'px');
      });

    },

