    /**
     * Abstract graphs generation
     * @return {void}
     */
    graphs: function(){

      /**
       * Doughnut graph
       * @sample usage:
       *
       *  <div class="row" data-chart="doughnut">
            <div class="col-xs-7">
            <canvas></canvas>
            <input type="hidden" value="27" name="27% Resources &amp; Infrastructure" data-color="#002F9C">
            <input type="hidden" value="20" name="20% Information &amp; Communication Technology" data-color="#3257AE">
            <input type="hidden" value="16" name="16% Financials &amp; Real Estate" data-color="#637FC1">
            <input type="hidden" value="13" name="13% Health, Personal Care &amp; Leisure" data-color="#7C93CA">
          </div>
            <div class="col-xs-5" data-labels></div>
          </div>
       *
       * see T7-v2 for demo
       * @return {object} chart object
       */

      $('[data-chart="doughnut"]').each(function(){
        var $this = $(this),
          data = [],
          options = {},
          $labels = $('<ul></ul>').addClass('doughnut-labels'),
          $canvas = $(this).find('canvas');

        $canvas.attr('width', $canvas.parent().outerWidth() - 40);
        $canvas.attr('height', $canvas.parent().outerWidth() - 40);

        $(this).find('input').each(function(){
          var $that = $(this);
          data.push({
            value: Number($that.val()),
            color: $that.data('color')
          });
          $labels.append('<li><span style="background-color: ' + $that.data('color') + '"></span>' + $that.attr('name') + '</li>');
        });

        if (!Modernizr.canvas || Modernizr.touch) {
          options.animation = false;
        }

        $this.find('[data-labels]').html($labels);
        $canvas[0].getContext('2d').clearRect (0, 0, $canvas.attr('width'), $canvas.attr('height'));
        setTimeout(function(){
          return new window.Chart($canvas[0].getContext('2d')).Doughnut(data, options);
        }, 30);
      });

      /**
       * Bar graph
       *
       * @sample usage:
       *
       *
       *  <div class="box-bg" data-chart="bar" data-height-md="graph" data-height-lg="graph">
            <canvas></canvas>
            <input type="hidden" value="65,59,90,81,56,55" name="55 Global Fund" data-color="#3257AE">
            <input type="hidden" value="28,48,40,19,96,27" name="Sector Average" data-color="#7C93CA">
            <div data-labels></div>
          </div>
       * @return {object} chart object
       */

      $('[data-chart="bar"]').each(function(){
        var $this = $(this),
          data = [],
          options = {},
          $labels = $('<ul></ul>').addClass('bar-labels'),
          $canvas = $(this).find('canvas');

        $canvas.attr('width', $canvas.parent().outerWidth());
        $canvas.attr('height', $canvas.parent().outerWidth()/1.5);

        data.datasets = [];
        $(this).find('input').each(function(){
          var $that = $(this);
          data.datasets.push({
            data: $that.val().split(',').map(Number),
            fillColor: $that.data('color'),
            strokeColor: $that.data('color')
          });
          $labels.append('<li><span style="background-color: ' + $that.data('color') + '"></span>' + $that.attr('name') + '</li>');
        });

        data.labels = [];
        var length = data.datasets[0].data.length;
        while(--length){
          data.labels.push('');
        }

        if (!Modernizr.canvas || Modernizr.touch) {
          options.animation = false;
        }

        $this.find('[data-labels]').html($labels);
        $canvas[0].getContext('2d').clearRect (0, 0, $canvas.attr('width'), $canvas.attr('height'));
        setTimeout(function(){
          new window.Chart($canvas[0].getContext('2d')).Bar(data, options);
        }, 30);
      });

    },

