    /**
     * Abstract sliders
     * @return {void}
     */
    sliders: function(){
			
      //trigger sliding according if we pressed minus or plus in custom buttons
      var flag = false;
      var slide = function(e){
      	if (!flag) {
      		flag = true;
      		setTimeout(function(){ 
      			flag = false; 
      		}, 100);
      		
	        e.preventDefault();
	        var $this = $(this),
	          $input = $this.data('target') ? $($this.data('target')) : $this.siblings('input'),
	          $slider = $input.siblings('.slider-wrap'),
	          isPlus = $this.data('sliderControl') === 'plus';
	
	        var step = $(this).data('step') || $input.data('step') || 1,
	          val = Number($input.val());
	
	        var result = isPlus ? val + step : val - step;
	        $slider.slider('value', result);
      	}
      };

      //bind events on external plus and minus buttons
      $('[data-slider-control]').on('click.app.slider', slide);

      //loop all slider hidden inputs and create sliders for it
      $('[data-slider]').each(function(){
      	var $this = $(this),
          $thisId = $this.attr('id'),
          $wrap = $('<div id="slider-' + $thisId + '" class="slider-wrap"></div>'),
          $info = $this.parent().parent().find('[data-slider-val]'),
          round = $this.data('round') || false;

        $info.text(APP.helpers.numbers.money($this.val()));

        //init the slider
        $wrap.slider({
          range: $this.data('range') || 'max',
          min: $this.data('min') || 0,
          max: $this.data('max') || false,
          value: $this.val(),
          step: $this.data('step') || false,
          slide: function( event, ui ) {
            $this.val(ui.value).trigger('slide.app.slider');
            $info.text(APP.helpers.numbers.money(ui.value, round));
          },
          change: function( event, ui ) {
            $this.val(ui.value).trigger('change.app.slider').trigger('slide.app.slider');
            $info.text(APP.helpers.numbers.money(ui.value, round));
          }
        });

        //add all necessary wrappers
        $this.parent().append($wrap);
        var $minus = $('<button type="button" class="btn btn-secondary btn-sm slider-button slider-minus" data-slider-control="minus"><i class="glyphicon glyphicon-minus"></i></button>');
        var $plus = $('<button type="button" class="btn btn-secondary btn-sm slider-button slider-plus" data-slider-control="plus"><i class="glyphicon glyphicon-plus"></i></button>');
        $this.parent().append($minus);
        $this.parent().append($plus);

        //bind events on plus and minus buttons
        $plus.on('touchstart.app.slider, click.app.slider', slide);
        $minus.on('touchstart.app.slider, click.app.slider', slide);
      });
    },

