(function (window, document, APP, $, Modernizr, undefined) {
  'use strict';


  /**
   * Custom success handlers for the calculators
   * @type {Object}
   */
  APP.forms['arf-or-annuity'] = {
		  
		init: function(){
	  
	  		$('.sort-container')
	  		.sortable({
	  			containment: 'parent',
	  			handle: '.item-container',
	  			tolerance: 'pointer',
	  			helper: 'original',
	  			//start: fixHelper,
	  			cursor: "move",
	  			stop: function( event, ui ) {
	  				calculate();
	  			}
	  		});
	  		
  		},

  	    success: function(form, data){
  			hasCalculated = 1;
  			show_results();
  			calculate();
  		}
		  
  };

	var hasCalculated = 0;

	function fixHelper( e, ui ) {
		var $ctr = $(this);
		ui.helper
		.addClass('mx-state-moving ui-corner-all')
		.outerWidth($ctr.outerWidth())
		.find('.mx-content-hover')
		.removeClass('mx-content-hover')
		.end();
	}


	function calculate() {
		if(hasCalculated == 0) 
			return;

		var arf_score = 0;
		var annuity_score = 0;
		var i = 1;
		hasCalculated = 1;

		$(function(){
			$('.sort-container>div>div').each(function(){
				var thisid = $(this).attr('id');

				if($( this ).hasClass( "arf" )) {
					arf_score = arf_score + i;
				} else {
					annuity_score = annuity_score + i;
				}
				i++;
			});
		});

		var currentLoser = $(".loser").text();
		$(".winner").empty();
		$(".loser").empty();

		if (parseInt(annuity_score) < parseInt(arf_score)) {
			$(".winner").append("Annuity");
			$(".winner").css("background-color", $(".result.annuity").css("background-color"));
			$(".winner").css("color", $(".result.annuity p").css("color"));
			$(".loser").append("ARF");
			$(".loser").css("background-color", $(".result.arf").css("background-color"));
			$(".loser").css("color", $(".result.arf p").css("color"));
			$("#returnToToolAnnuity").removeClass("hide");
			$("#returnToToolArf").addClass("hide");
		} else {
			$(".winner").append("ARF");
			$(".winner").css("background-color", $(".result.arf").css("background-color"));
			$(".winner").css("color", $(".result.arf p").css("color"));
			$(".loser").append("Annuity");
			$(".loser").css("background-color", $(".result.annuity").css("background-color"));
			$(".loser").css("color", $(".result.annuity p").css("color"));
			$("#returnToToolArf").removeClass("hide");
			$("#returnToToolAnnuity").addClass("hide");
		}

		if (currentLoser == "" || currentLoser != $(".loser").text()) {
		    $(".winner").flash();
		    $(".loser").flash();
		}

		//reset scores
		arf_score = 0;
		annuity_score = 0;
	}

	function show_results() {
		$(".remove-me").addClass("hide");
		$('.question').addClass('result');
        $('#result[data-collapse]').collapse('show');
        $('#key[data-collapse]').collapse('show');
	}

	jQuery.fn.flash = function () {
	   this.animate({ height: 'hide' });
	   this.animate({ height: 'show' });
	}
	
})(this, this.document, this.APP, this.jQuery, this.Modernizr);








