(function (window, document, APP, $, Modernizr, undefined) {
  'use strict';


  /**
   * Custom success handlers for the calculators
   * @type {Object}
   */
  APP.forms['capitalisation-calc'] = {

    /**
     * Custom events for calculators
     * @return {[type]} [description]
     */
    init: function(){},

    /**
     * Custom validation rules that cant be set up inline
     * @return {[type]} [description]
     */
    validate: {},


    /**
     * Success handler after the form is validated, so any ajax/static js calculations goes here
     * @param  {dom} form
     * @param  {object} data - form data as object
     * @return {void}
     */
    success: function(form, data){
      //here goes calculator logic
    	
    	$('[data-result]').blink();
    	calculate();
    	
    	function calculate()  
    	{
     		var income = document.getElementById('monthlyIncome').value;
    		var term = document.getElementById('term').value;
    		var inflationProtection = document.getElementById('inflationProtection').value;

    		if (! income ) {
    			income = 5000; //default
    			document.getElementById('monthlyIncome').value = income;
    		}
    		else if (income < 500) {
    			income = 500; 
    			document.getElementById('monthlyIncome').value = income;	
    		}
    		else if (income > 30000) {
    			income = 30000; 
    			document.getElementById('monthlyIncome').value = income;	
    		}
    		
    		if (! term ) {
    			term = 10; //default
    			document.getElementById('term').value = term;
    		}
    		else if (term < 2) {
    			term = 2; 
    			document.getElementById('term').value = term;
    		}	
    		else if (term > 40) {
    			term = 40; 
    			document.getElementById('term').value = term;
    		}	
    		
    		var inflation = 0.03; // value for inflationProtection=="No"
    		if (inflationProtection=="Yes") {
    			inflation = -0.02;
    		}
    		

    		var capitalisationValue = 12 * income * (1-1/Math.pow(1+inflation,term)) / inflation;
    		if (capitalisationValue < 0)
    			capitalisationValue = -capitalisationValue;
		
    		
    		document.getElementById('capitalisationValue').innerHTML = capitalisationValue.toFixed(2);

    		
        	$('[data-result]').currency({ region: 'EUR'});
    	}
    }
  };



})(this, this.document, this.APP, this.jQuery, this.Modernizr);

