// Tidy up the charts after they are added
function tidyUpFundDetailsCharts() {
	APP.events.graphs();
	APP.events.sameHeights('graph');
	APP.isDevice('xsmall', function () {
		APP.events.sameHeights('data-height-xs');
	});
	APP.isDevice('small', function () {
		APP.events.sameHeights('data-height-sm');
	});
	APP.isDevice('medium', function () {
		APP.events.sameHeights('data-height-md');
	});
	APP.isDevice('large', function () {
		APP.events.sameHeights('data-height-lg');
	});
}

function drawPieChart(){

	var pieSettings = {};
	if(window.innerWidth <= 990 && window.innerWidth >= 767) {
		pieSettings = {
			innerRadius: 0.3,
			radius: 0.8,
			offset: {left: -50}
		}
	} else {
		pieSettings = {
			innerRadius: 0.5
		}
	}
	$.extend(pieSettings, {show: true});
	
	if(dataAvailable){		
		$("#assetSplit").removeClass("hide");
		$("#assetSplitNote").removeClass("hide");
		$.plot($("#assetSplitChart"), chartData, {
			series: {
				pie: pieSettings
			}
		});
	}
}

